svg.script:
var end = document.getElementById("endContainer");
var cycle = 0;
end.addEventListener("click", function(e){
	cycle++;
	cycle %= 3;
	if(cycle == 0)
		document.getElementById("stripContainer").style.fill = "#5AB738";
	if(cycle == 1)
		document.getElementById("stripContainer").style.fill = "#B7385A";
	if(cycle == 2)
		document.getElementById("stripContainer").style.fill = "#385AB7";
});
